    i numeric = 1;
    _id numeric;
BEGIN
    raise notice 'start';
        for V in select jsonb_array_elements((_data->>'movies')::jsonb) loop
                _id = (V->>'id')::numeric;
                select count(*) into aux from videos where (id = _id);
                raise notice 'Video Exists: %', aux;
                if ((V->>'tmdb')::jsonb is null) then
                    _tmdb = ('{}')::jsonb;
                else
                    _tmdb =(V->>'tmdb')::jsonb;
