                if ((V->>'meta')::jsonb is null) then
                    _meta = ('{}')::jsonb;
                else
                    _meta =(V->>'meta')::jsonb;
                end if;

                if (aux = 0) then
                    insert into videos (id, name, type, tmdb_id, tmdb, tmdb_rank, url, tvshowid, info,
