                                        downloadstatus, status, categories,
                                        httpcode)
                    values (_id, V->>'name', V->>'type', V->>'tmdb_id',_tmdb, (V->>'tmdb_rank')::numeric, V->>'url',(V->>'tvshowid')::numeric,(V->>'info')::jsonb,
                            _meta, (V->>'season')::numeric, (V->>'episode')::numeric, V->>'filename', V->>'originalname', V->>'localtodo', V->>'ext',
                            V->>'bucket', V->>'cloudpath', V->>'localpath',
                            'pending', V->>'status',(V->>'categories')::jsonb,
                            (V->>'httpcode')::numeric);
                    i = i+1;
                else
                    update videos
                    set status = V->>'status',
                        categories = (V->>'categories')::jsonb,
                        httpcode = (V->>'httpcode')::numeric, --issues= (V->>'issues')::jsonb,
                        meta = _meta,
                        tmdb_id = V->>'tmdb_id',
                        tmdb_rank = (V->>'tmdb_rank')::numeric,
                        tmdb = _tmdb,
                        type = V->>'type',
                        name =V->>'name',
