                        tvshowid = (V->>'tvshowid')::numeric,
                        url = V->>'url',
                        bucket = V->>'bucket',
                        cloudpath = V->>'cloudpath',
                        localpath = V->>'localpath',
                        season = (V->>'season')::numeric,
                        episode = (V->>'episode')::numeric,
                        modified_at = current_timestamp
                    where id = _id;
                end if;
            end loop;

    return '{"status":"success"}';
END;
$$;

